mapper.select:
select user_id,  email, mobile, pwd, status, create_time, update_time,nick_name
from v_user
where user_id = #{userId,jdbcType=VARCHAR}

select mobile
from v_user
where user_id = #{userId,jdbcType=VARCHAR}

select user_id,pwd,email,mobile, nick_name
 from v_user where email = #{email,jdbcType=VARCHAR}
limit 1

select user_id,pwd,email,mobile, nick_name
 from v_user where mobile = #{mobile,jdbcType=VARCHAR}
limit 1

select user_id from v_user where
mobile in
#{mobile}
or
email in
#{email}
